export {};

declare global {
  type TRelationStyle = 1 | 2 | 3 | 4 | 5;

  interface IKeyValue {
    [key: string]: any;
  }

  interface IArrData {
    dataSource: Array<IKeyVal>;
    basicAttribute?: IBasicAttribute;
  }

  interface IObjData {
    dataSource: IKeyVal;
    basicAttribute?: IBasicAttribute;
  }

  interface IBasicAttribute {
    backgroundColor?: string;
    backgroundImage?: string;
    color?: string;
    activeColor?: string;
    relationStyle?: TRelationStyle;
    title?: string;
    monitor?: IMonitor;
    rowStyle?: number;
    colStyle?: number;
    visibleName?: number;
  }

  interface IMonitor {
    componentName: string;
    monitorName: string;
    is: boolean;
  }
}
